mapper.select:
select
lert.app_id, app_code, app_name, logo, url, status,remark, create_by, create_time, update_by,
update_time, theme, lert.type, app_Language
from sys_app lert left join sys_tenant_app on lert.app_id = sys_tenant_app.app_id

    tenant_id = #{tenantId,jdbcType=VARCHAR}

      and lert.app_id = #{appId}


      and app_code = #{appCode}


      and app_name like concat ('%',#{appName,jdbcType=VARCHAR},'%')

     and del_flag = 0
