mapper.delete:
delete
from sys_org_app

    org_id = #{orgId}

        and type =  #{type}

delete
from sys_org_app
where org_id = #{orgId} and app_id = #{appId}

delete sys_org_app
from sys_org_app ,sys_org
where sys_org_app.org_id = sys_org.org_id and sys_org.org_id = #{orgId} and sys_org_app.app_id = #{appId}
