mapper.select:
select role_id, role_code, role_name,app_name, sr.app_id, sr.remark ,sr.create_time ,sr.create_by ,sr.update_time,sr.update_by  from sys_role sr left join sys_app sa on sr.app_id = sa.app_id

         1=1

        and sr.app_id = #{appId}


       and role_code = #{roleCode}


        and role_name = #{roleName}

        and sr.del_flag = 0
        and sr.tenant_id = #{tenantId}
