mapper.select:
select

from sys_tenant

    1=1

        and tenant_code = #{tenantCode,jdbcType=VARCHAR} or tenant_name = #{tenantName,jdbcType=VARCHAR}


        and tenant_name = #{tenantName,jdbcType=VARCHAR}

    and del_flag=0

mapper.update:
update sys_tenant
set del_flag = 1
where tenant_id = #{tenantId,jdbcType=VARCHAR}
