mapper.update:
update sys_user set user_name=#{username}, password =#{password},status=#{status},
org_id=#{orgId},nickname=#{nickname}, phone=#{phone},avatar=#{avatar},email=#{email},
update_by=#{updateBy},type=#{type}, update_time = now()  where user_id = #{userId}

update sys_user
set del_flag=1
where org_id = #{orgId}

mapper.select:
select user_id, user_name,status, org_id, tenant_id, nickname, phone, avatar, email,
create_by, create_time, update_by, update_time, type from sys_user
where tenant_id = #{tenantId,jdbcType=VARCHAR}

       and user_name=#{username}


       and org_id=#{orgId}


        and nickname=#{nickname}

    and del_flag = 0
